mapper.select:
select *
from arruser join course on arruser.course_id = corse.course_id
where student_id = #{userId}

select *
from arruser join course on arruser.course_id = corse.course_id
where teacher_id = #{userId} and arrange_date >= #{date1} and arrange_date <= #{date2}

select *
from arruser join course on arruser.course_id = corse.course_id
where user_id = #{userId} and arrange_date >= #{date1} and arrange_date <= #{date2}

select *
from arruser join course on arruser.course_id = corse.course_id
where arrange_date >= #{date1} and arrange_date <= #{date2}
